-- 给link前面加上item:
local function InsertItemString(table)
    if type(table) == "table" then
        local t = {}
        for k, v in pairs(table) do
            if type(v) == "table" then
                t[k] = InsertItemString(v)
            else
                t[k] = "item:" .. table[k]
            end
        end
        return t
    end
end
for _, FB in pairs(BG.FBtable) do
    BG.Loot[FB] = InsertItemString(BG.Loot[FB])
